//import './css/write-page.css';
import { useState } from 'react';
import {useParams} from 'react';

function ViewPage() {
  const params = useParams();
  const contentArray =  window.localStorage.getItem("contentArray");

  let arr;
  if(contentArray == null) {
    arr = [];
  }else {
    arr = JSON.parse(contentArray);
  }

  // 컴포넌트가 바로 업데이트 될 수있도록 useSate를 사용해, props 를 업데이트해준다.
  let [parsedData, setParsedData] = useState(arr);

  return (
    <div className="view-page">
      <div className='sub-inner'>
        <h2 className='sub-title'>뷰페이지</h2>
        <form>
          <div className="view-content">
              뷰콘텐츠 노출
          </div>
          {/* <textarea
            value={content}
            onChange={onChange}
            placeholder="내용을 입력해주세요."
          >            
          </textarea> */}
          


          <div className='button-wrap'>
            <button 
            type='button' 
            className='button button-normal'
            > 수정
            </button>
            <button 
            type='submit'
            className='button button-ok'
            > 삭제
            </button>
          </div>

        </form>
      </div>
    </div>
  );
}

export default ViewPage;
