import { useState } from 'react';
import { useParams } from 'react-router-dom';

function ViewPage() {
  const params = useParams();
  const contentArray =  window.localStorage.getItem("contentArray");
  let [parsedData, setParsedData] = useState(JSON.parse(contentArray));
  var thisCont = parsedData.filter(
    (stroageItem) => stroageItem.key == params.id
  );
  console.log(thisCont[0].value);
  const thisVal = thisCont.value

  return (
    <div className="view-page">
      <div className='sub-inner'>
        <h2 className='sub-title'>뷰페이지</h2>
        <form>
          <div className="view-content">
              뷰콘텐츠 노출
          </div>

          {/* value 중에 내가 원하는 값만 = param 값!!!!!!!!!! */}
          
          <textarea
            placeholder="내용을 입력해주세요."
          >            
          </textarea>
           


          <div className='button-wrap'>
            <button 
            type='button' 
            className='button button-normal'
            > 수정
            </button>
            <button 
            type='submit'
            className='button button-ok'
            > 삭제
            </button>
          </div>

        </form>
      </div>
    </div>
  );
}

export default ViewPage;
