import './write-page.css';
import { useState } from 'react';
import {Link} from 'react-router-dom';


function WritePage() {
  const storeageData = window.localStorage.getItem('dataArray');

  //1. 글쓴것을 저장하는 useState
  //2. 글쓴것을 배열로 저장할 useState

  const [content, setContent] = useState('');
  const [contents, setContents] = useState([]);
  const onChange = (event) => setContent(event.target.value);

  const onSubmit = (event) => {

    event.preventDefault();

    //3. 글쓴것을 object화 하기
    let setContent = { value : content };
     //노마드코딩이 알려줌-> 블로그
    //4. 배열에 전배열과 같이 넣기
    setContents(currentArray => [content, ...currentArray]);
  }

  console.log(contents);
  //5. logalstorage에 저장하기
  window.localStorage.setItem("contents", JSON.stringify(contents));


  return (
    <div className="write-page">
      <div className='sub-inner'>
        <h2 className='sub-title'>작성하기</h2>
        <form>
          <textarea
            value={content}
            placeholder="내용을 입력해주세요."
            onChange={onChange}
          >            
          </textarea>

          <div className='button-wrap'>
            <button 
            type='button' 
            className='button button-normal'
            >취소
            </button>
            <Link to="/"
            className='button button-normal'
            >목록으로</Link>
            <button 
            type='submit'
            className='button button-ok'
            onClick={onSubmit}
            >확인
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default WritePage;
