import * as React from 'react';
import { useState } from 'react';
import {Link} from 'react-router-dom';

function ListPage() {
    
    
    const contentArray =  window.localStorage.getItem("contentArray");
    const parsedData = JSON.parse(contentArray);
    //console.log(parsedData);

    const actionDel = (e, key) => {
      // 변화된 배열을 update 해주는 usestate
      const [newArray, setNewArray] = useState('');
      e.preventDefault();
      setNewArray = parsedData.filter((item) => item.key !== key);

      window.localStorage.setItem("contentArray", JSON.stringify(newArray));
    }

    
      return (
        <div className="list-page">
          <div className='sub-inner'>
            <h2 className='sub-title'>리스트 페이지</h2>
            <form>
              <ul className="list-content">
                <li className="list-header">
                  <div>번호</div>
                  <div>제목</div>
                  <div>관리</div>
                </li>
                  {parsedData != null ? (
                    parsedData.map((item, index) => 
                    <li className="list-body" key={item.key}>
                      <div>{item.key}</div>
                      <div className="subject">{item.value}</div>
                      <div>
                        <button 
                          type="button" 
                          className="button-del"
                          onClick={(e) =>     {actionDel(e, item.key)}
                          }
                        >
                          {/* 01. 클릭한 애의 키값 받아오기 */}
                          {/* 02. 키값이 지금숫자인 애를 배열에서 필터링*/}
                          삭제</button>
                      </div>
                    </li>
                    )
                  ) : (
                    <li className="list-empty">등록된 게시글 없습니다.</li>
                  )}
              </ul>
    
              <div className='button-wrap'>
                <button 
                type='button' 
                className='button button-normal'
                > 수정
                </button>
                <button 
                type='submit'
                className='button button-normal'
                > 삭제
                </button>
                <Link 
                  to={`/write`}
                  className='button button-ok'
                > 글쓰기
                </Link>
              </div>
    
            </form>
          </div>
        </div>
      );
    }
    
    export default ListPage;
