import './write-page.css';
import { useState } from 'react';
import {Link} from 'react-router-dom';


function WritePage() {
  const [title, setTitle] = useState('');
  // const [content, setContent] = useState('');
  //const key = dataArray.length + 1;

  const getTitle = (event) => {
    setTitle(event.target.value);
    //console.log(title);
  }

  // 1. 상세페이지에 접속하면, 즉, 컴포넌트가 로딩되면,, 
  const getData = (event) => {
    event.preventDefault();

    console.log('1', title);
    // 2. localStoreage에서 데이터를 꺼낸다.
    var myArr = localStorage.getItem("myArr");


    // 최초 접속시 localStorage에 데이터 자료가 없을 경우 새로운 배열을 생성
    // 혹은 사용자 모두에게 dataArray 자료형을 localStorage에 넣는 방식
    if (myArr == null) {
      myArr = [];
    } else {
      // myArr에서 자료를 꺼내 따옴표를 제거하고 다시 myArr 에 저장한다.
      // JSON.parse : 타입을 변환한다.
      myArr = JSON.parse(myArr);
    }

    // 3. 현재 title을 myArr에 저장한다.
    myArr.push(title);

    console.log('2', myArr);


    // 4. 중복된 데이터를 넣지 않는 set 자료형에 myArr를 담아 중복을 제거
    myArr = new setTitle(myArr);

    // 중복 제거된 set 자료형의 myArr를 일반 배열로 변경한다.
    myArr = [...myArr];


    // 5. localStorage에 데이터를 json 자료형으로 저장한다.
    localStorage.setItem('boardData', JSON.stringify(myArr));
  }

  return (
    <div className="write-page">
      <div className='sub-inner'>
        <h2 className='sub-title'>작성하기</h2>
        <input 
            type="text"
            placeholder="제목을 입력해주세요."
            value={title}
            onChange={getTitle}
          >
          </input>
          <textarea
           // value={content}
            placeholder="내용을 입력해주세요."
            //onChange={getContent}
          >            
          </textarea>

          <div className='button-wrap'>
            <button 
            type='button' 
            className='button button-normal'
            >취소
            </button>
            <Link to="/"
            className='button button-normal'
            >목록으로</Link>
            <button 
            type='submit'
            className='button button-ok'
            onClick={getData}
            >확인
            </button>
          </div>
      </div>
    </div>
  );
}

export default WritePage;
